import React from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import '../../../Downloads/lesson-22-master/lesson-22-master/src/App.css';

import HomePage from '../../../Downloads/lesson-22-master/lesson-22-master/src/pages/homepage/homepage.component';
import ShopPage from '../../../Downloads/lesson-22-master/lesson-22-master/src/pages/shop/shop.component';
import SignInAndSignUpPage from '../../../Downloads/lesson-22-master/lesson-22-master/src/pages/sign-in-and-sign-up/sign-in-and-sign-up.component';
import CheckoutPage from '../../../Downloads/lesson-22-master/lesson-22-master/src/pages/checkout/checkout.component';

import Header from '../../../Downloads/lesson-22-master/lesson-22-master/src/components/header/header.component';

import { auth, createUserProfileDocument } from '../../../Downloads/lesson-22-master/lesson-22-master/src/firebase/firebase.utils';

import { setCurrentUser } from '../../../Downloads/lesson-22-master/lesson-22-master/src/redux/user/user.actions';
import { selectCurrentUser } from '../../../Downloads/lesson-22-master/lesson-22-master/src/redux/user/user.selectors';

class App extends React.Component {
  unsubscribeFromAuth = null;

  componentDidMount() {
    const { setCurrentUser } = this.props;

    this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth);

        userRef.onSnapshot(snapShot => {
          setCurrentUser({
            id: snapShot.id,
            ...snapShot.data()
          });
        });
      }

      setCurrentUser(userAuth);
    });
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }

  render() {
    return (
      <div>
        <Header />
        <Switch>
          <Route exact path='/' component={HomePage} />
          <Route path='/shop' component={ShopPage} />
          <Route exact path='/checkout' component={CheckoutPage} />
          <Route
            exact
            path='/signin'
            render={() =>
              this.props.currentUser ? (
                <Redirect to='/' />
              ) : (
                <SignInAndSignUpPage />
              )
            }
          />
        </Switch>
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser
});

const mapDispatchToProps = dispatch => ({
  setCurrentUser: user => dispatch(setCurrentUser(user))
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
