import firebase from "firebase/app";
import 'firebase/firestore'
import 'firebase/auth'

const config = {
    apiKey: "AIzaSyCRlJCmhpf0ZovaUYXseXNr9n-_hbx7_P0",
    authDomain: "shop-db-40826.firebaseapp.com",
    projectId: "shop-db-40826",
    storageBucket: "shop-db-40826.appspot.com",
    messagingSenderId: "49013690694",
    appId: "1:49013690694:web:454c9838a7fac56c8a5547",
    measurementId: "G-DVY7SL63HH"
}

export const createUserProfileDocument = async (userAuth, additionalData) => {
    if (!userAuth) return;
    const userRef = firestore.doc(`users/${userAuth.uid}`)
    const collectionRef = firebase.collection('users');

    const snapshot = await userRef.get() //to represent the data
    const collectionSnapshot = await collectionRef.get()
    console.log({collection: collectionSnapshot.docs.map(doc => doc.data())})

    if(!snapshot.exists){
        const { displayName, email} = userAuth
        const createdAt = new Date()

        try {
            await userRef.set(
                {
                    displayName,
                    email,
                    createdAt,
                    ...additionalData
                }
            )
        }catch(e){
            console.log('error creating user', e)
        }
    }
    return userRef
}

firebase.initializeApp(config)

export const auth = firebase.auth()
export const firestore = firebase.firestore()

const provider = new firebase.auth.GoogleAuthProvider()
provider.setCustomParameters({prompt: 'select_account'})
export const signInWithGoogle = () => auth.signInWithPopup(provider);

export default firebase
